var stringsArray = wordsSample.match(/[a-zA-Z']+/g);

function Node (word) {
    this.word = word;
    this.count = 1;     
    this.left = null;
    this.right = null;
    this.incCoincidence = function (){
          this.count++;
    }
    this.setLeftNode = function(node){
          this.left = node;
    }
    this.setRightNode = function(node){
          this.right = node;
    }
    this.getData = function(){
          return this.word;
    }
    this.compareNode = function(node){
          return this.word.localeCompare(node.getData());
    }
    this.getCount = function(){
        return this.count;
    }
    this.getLeft = function(){
        return this.left;
    }
    this.getRight = function(){
        return this.right;
    }
    this.hasLeft = function(){
        return this.left != null;
    }
    this.hasRight = function(){
        return this.right != null;
    }
}

var cycle = function(node,root){
     var cmp = node.compareNode(root);
     if(cmp == 0){
         root.incCoincidence();
     } else if(cmp<0){
         if(root.hasLeft()){
             cycle(node, root.getLeft());
         } else {
             root.setLeftNode(node);
         }
     } else {
         if(root.hasRight()){
             cycle(node, root.getRight());
         } else {
             root.setRightNode(node);
         }
     }
}

